mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM bank_worker_base.bank_branch_staff_account
WHERE 1=1

  AND data_status = #{dataStatus,jdbcType=VARCHAR}
;

SELECT A.account_id, A.bank_code, D.bank_name, A.branch_code, E.branch_name, A.system_id, C.system_name, A.staff_account, A.staff_password, A.data_status, CASE WHEN A.data_status = 'N' THEN '正常' WHEN A.data_status = 'F' THEN '冻结' WHEN A.data_status = 'D' THEN '删除' ELSE '未知数据状态' END AS data_status_text, B.admin_name AS create_user, DATE_FORMAT(A.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, B.admin_name AS update_user, DATE_FORMAT(A.update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM bank_worker_base.bank_branch_staff_account A LEFT JOIN bank_worker_base.system_admin B ON A.create_user = B.admin_id LEFT JOIN bank_worker_base.system_setting C ON A.system_id = C.system_id LEFT JOIN bank_worker_base.bank D ON A.bank_code = D.bank_code LEFT JOIN bank_worker_base.bank_branch E ON A.branch_code = E.branch_code WHERE 1=1
AND A.data_status = #{arg2,jdbcType=VARCHAR}
ORDER BY A.update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE bank_worker_base.bank_branch_staff_account SET
  staff_account = #{account,jdbcType=VARCHAR},
  staff_password = #{password,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE account_id = #{accountID,jdbcType=INTEGER}

UPDATE bank_worker_base.bank_branch_staff_account SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE account_id = #{accountID,jdbcType=INTEGER}
