mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM bank_worker_base.bank
WHERE 1=1

  AND data_status = #{dataStatus,jdbcType=VARCHAR}
;

SELECT bank_id, bank_code, bank_name, bank_logo, data_status, CASE WHEN data_status = 'N' THEN '正常' WHEN data_status = 'F' THEN '冻结' WHEN data_status = 'D' THEN '删除' ELSE '未知数据状态' END AS data_status_text, create_user, DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s') AS create_time, update_user, DATE_FORMAT(update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM bank_worker_base.bank WHERE 1=1
AND data_status = #{arg2,jdbcType=VARCHAR}
ORDER BY update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE bank_worker_base.bank SET
  bank_code = #{bankCode,jdbcType=VARCHAR},
  bank_name = #{bankName,jdbcType=VARCHAR},
  bank_logo = #{bankLogo,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE bank_id = #{bankID,jdbcType=INTEGER}

UPDATE bank_worker_base.bank SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE bank_id = #{bankID,jdbcType=INTEGER}
