mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM bank_worker_base.system_admin
WHERE 1=1

  AND data_status = #{dataStatus,jdbcType=VARCHAR}
;

SELECT admin_id, admin_name, admin_photo, admin_cellphone, data_status, CASE WHEN data_status = 'N' THEN '正常' WHEN data_status = 'F' THEN '冻结' WHEN data_status = 'D' THEN '删除' ELSE '未知数据状态' END AS data_status_text, create_user, DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s') AS create_time, update_user, DATE_FORMAT(update_time, '%Y-%m-%d %H:%i:%s') AS update_time FROM bank_worker_base.system_admin WHERE 1=1
AND data_status = #{arg2,jdbcType=VARCHAR}
ORDER BY update_time DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE bank_worker_base.system_admin SET
  admin_name = #{adminName,jdbcType=VARCHAR},
  admin_photo = #{adminPhoto,jdbcType=VARCHAR},
  admin_cellphone = #{adminCellphone,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE admin_id = #{adminID,jdbcType=INTEGER}

UPDATE bank_worker_base.system_admin SET
  data_status = #{dataStatus,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE admin_id = #{adminID,jdbcType=INTEGER}

UPDATE bank_worker_base.system_admin SET
  admin_password = #{adminPassword,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR}
WHERE admin_id = #{adminID,jdbcType=INTEGER}
